package calculator.xwg;import java.util.LinkedList;public class RootFun extends CalculateFunction {	String getName(){		return "root";	}		boolean execute(LinkedList<Complex> paraList, EvaluateContext context){		if(paraList.size() != 2)	    {	        context.setErrorMessage(getName(), R.string.error_invalid_parameter_count);	        return false;	    }	    Complex x = paraList.removeFirst();	    if(x.i != 0)	    {	    	context.setErrorMessage(getName(), R.string.error_invalid_date_type);	        return false;	    }	    Complex y = paraList.removeFirst();	    if(y.r == 0)	    {	    	context.setErrorMessage(getName(), R.string.error_invalid_input);	        return false;	    }	    if(y.i != 0)	    {	    	context.setErrorMessage(getName(), R.string.error_invalid_date_type);	        return false;	    }	    context.setCurrentResult(new Complex(Math.pow(x.r, 1/y.r)));	    return checkResult(context);	}}